            if (rate === 0)
                return;

            if (!allBatches || allBatches.length === 0)
                return;

            var frames = [];
            for (var i = 0, il = allBatches.length ; i < il ; i ++) {
                frames.push(rate > 0 ? i : (il - i - 1));
            }

            var freq = 1000 / Math.abs(rate);
            var index = 0;

